import logo from "./logo.svg";
import { useState } from "react";
import "./App.css";

function App() {
  const [address, setAddress] = useState();
  const [error, setError] = useState();
  const [amount, setAmount] = useState();
  return (
    <div>
      {!address ? (
        <></>
      ) : (
        <div className="flex items-center justify-center bg-cyan-100">
          <p>{address}</p>
        </div>
      )}
      <div className="flex items-center justify-center min-h-screen bg-cyan-100">
        {!address ? (
          <button
            type="button"
            className="mt-2 mb-2 text-white bg-gradient-to-r from-cyan-500 to-blue-500 hover:bg-gradient-to-bl focus:ring-4 focus:outline-none focus:ring-cyan-300 dark:focus:ring-cyan-800 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-2 mb-2"
          >
            connect
          </button>
        ) : (
          <form
            className="bg-cyan-200 shadow-md rounded px-8 pt-6 pb-8 mb-4"
            onSubmit={tranfer}
          >
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Address
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="username"
                type="text"
                placeholder="address"
                required
                value={receiver}
                onChange={(event) => {
                  setReceiver(event.target.value);
                }}
              />
            </div>
            <div className="mb-6">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Ether
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
                id="value"
                type="text"
                placeholder="Amount"
                value={amount}
                required
                onChange={(event) => {
                  setAmount(event.target.value);
                }}
              />
            </div>
            <div className="flex items-center justify-between">
              <button
                className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
                type="submit"
                // onSubmit={() => {
                //   tranfer();
                // }}
              >
                Transfer
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default App;
